(()=>{var o=document.querySelectorAll("#darkModeToggle, #darkModeToggleMobile, #darkModeToggleMobileMenu");o.forEach(t=>{t.addEventListener("click",()=>{document.documentElement.classList.toggle("dark"),localStorage.setItem("darkMode",document.documentElement.classList.contains("dark"))})});(localStorage.getItem("darkMode")==="true"||!localStorage.getItem("darkMode")&&window.matchMedia("(prefers-color-scheme: dark)").matches)&&document.documentElement.classList.add("dark");var d=document.getElementById("mobileMenuButton"),n=document.getElementById("closeMenu"),e=document.getElementById("mobileMenu");d.addEventListener("click",()=>{e.classList.remove("hidden")});n.addEventListener("click",()=>{e.classList.add("hidden")});console.log("Hello");})();
